import logo from './favicon.png';
import './App.css';
import React from 'react';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state ={
      logo: true
    }
    this.loading = this.loading.bind(this);
  }

  componentDidMount() {
    if(this.loading) {
      this.setState({
        logo: false
      })
    }
  }

  loading() {
    <div className="App">
      <img src='./wheel.gif' />
    </div>
  }

  render() {
    if(this.state.logo === false) {
      return (
        <div className="App">
          {this.loading}
        </div>
      );
    }

    if(this.state.logo === true) {
      return (
        <div className="App">
          {this.loading}
        </div>
      );
    }
  }
}

export default App;
