import React from 'react';
import './Game.css';
import Spritesheet from 'react-responsive-spritesheet';
import link from './link_sheet.png';

class Game extends React.Component {
  constructor(props) {
    super(props);
    this.state ={
        standingX: 0,
        standingY: -384
    }
    this.downCycle = this.downCycle.bind(this);
  }

  componentDidMount() {
  }

  downCycle() {
    setInterval(() => {
        if(this.state.standingX === -768) {
            this.setState({
                standingX: 0
            })
        }
        this.setState({
            standingX: this.state.standingX -96
        })
    }, 130)
  }

  render() {
    return(
      <div id="gameWindow">
        <div id="innerWindow">
            <div
                style={{
                    display: "inline-block",
                    height: "96px",
                    width: "96px",
                    backgroundImage: `url('https://i.ibb.co/PrTZX5C/link-sheet.png')`,
                    backgroundRepeat: "no-repeat",
                    backgroundPosition: `${this.state.standingX}px ${this.state.standingY}px`,
                    position: "absolute",
                    left: "37.5vw",
                    top: "36vh"
                }}
                onKeyDown={this.downCycle}
            />
        </div>
      </div>
    );
  }
}

export default Game;
