import logo from './favicon.png';
import wheel from './wheel.gif';
import './App.css';
import React from 'react';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state ={
      logo: true
    }
    this.loading = this.loading.bind(this);
  }

  componentDidMount() {
    setInterval(() => {
      this.setState({
        logo: false
      })
    }, 2000);
  }

  loading = () => (
    <img id='wheel' src={wheel} />
 );

  render() {
    if(this.state.logo === false) {
      return (
        <div className="App">
          Hello
        </div>
      );
    }

    if(this.state.logo === true) {
      return (
        <div className="App">
          {this.loading()}
        </div>
      );
    }
  }
}

export default App;
