import React from 'react';
import './Game.css';
import Spritesheet from 'react-responsive-spritesheet';
import link from './link_sheet.png';

class Game extends React.Component {
  constructor(props) {
    super(props);
    this.state ={
        standingX: 0,
        standingY: -192
    }
    this.standingCycle = this.standingCycle.bind(this);
  }

  componentDidMount() {
    setInterval(() => {
        if(this.state.standingX < -384) {
            this.setState({
                standingX: 0
            })
        }
        this.setState({
            standingX: this.state.standingX - 48
        })
    }, 300);

        // 384
  }

  standingCycle() {

  }

  render() {
    return(
      <div id="gameWindow">
        <div
            style={{
                display: "inline-block",
                height: "48px",
                width: "48px",
                backgroundImage: `url('https://i.ibb.co/wp9bJ3C/link-sheet.png')`,
                backgroundRepeat: "no-repeat",
                backgroundPosition: `0px -192px`,
                position: "absolute",
                left: "25vw",
                top: "15vh"
            }}

        />
        <img src={link} />
      </div>
    );
  }
}

export default Game;
